alex_action_10 = char 
alex_action_11 = smac 
alex_action_12 = rmac 
alex_action_13 = smacdef 
alex_action_14 = rmacdef 
alex_action_15 = special `andBegin` startcodes 
alex_action_16 = zero 
alex_action_17 = startcode 
alex_action_18 = special 
alex_action_19 = special `andBegin` 0 
